# Euclidean clusters
dat = t(data.frame(s10=series10, s11=series11, s12=series12,
                   s40=series40, s41=series41, s42=series42,
                   s50=series50, s51=series51, s52=series52))
hc <- hclust(dist(dat), "complete"); par(mar=c(3,1,1,5));plot(as.dendrogram(hc),horiz=T)
library(ctc)
write.table(hc2Newick(hc), file="euclidean.newick",row.names=FALSE,col.names=FALSE)
#
# DTW
#
library(dtw);distMatrix <- dist(dat, method="DTW")
hc <- hclust(distMatrix, method="complete");plot(hc)
write.table(hc2Newick(hc), file="dtw.newick",row.names=FALSE,col.names=FALSE)

